import httpClient from "@/utils/httpclient";
import VehicleForm from "@/components/vehicleForm";
export default {
  name: "bindVehicle",
  inject: ["reload"],
  components: {
    VehicleForm: VehicleForm
  },
  data: function data() {
    return {
      step1: true,
      step2: false,
      step3: false,
      deviceId: null,
      step1Button: true,
      imeiIsVaild: false,
      imeiSuccess: false,
      imeiDisable: false,
      step2Button: true,
      title: '',
      data1: {},
      data2: null,
      showTip: false,
      vin: '',
      vinDisable: false,
      vinIsVaild: false,
      vinSuccess: false,
      isClickStep2: false,
      vehicleId: ''
    };
  },
  watch: {
    deviceId: function deviceId(newV) {
      if (newV.length == 15) {
        this.deviceId = newV;
        this.imeiDisable = true;
        this.imeiIsVaild = false;
        this.imeiSuccess = true;
        this.step1Button = false;
      } else {
        this.imeiIsVaild = true;
      }
    },
    vin: function vin(newV) {
      if (newV.length == 17) {
        this.vin = newV;
        this.vinDisable = true;
        this.vinIsVaild = false;
        this.vinSuccess = false;
        this.step2Button = false;
        this.clickStep2();
      } else {
        this.vinIsVaild = true;
      }
    }
  },
  mounted: function mounted() {},
  methods: {
    reloadPage: function reloadPage() {
      window.location.reload();
    },
    unbind: function unbind() {
      var _this = this;

      httpClient.post('/api/v1/vehicle/unbind/device', {
        deviceMac: this.deviceId,
        vin: this.vin
      }).then(function (response) {
        if (response.data.code == 200) {
          _this.vin = '';
          _this.vinDisable = false;
          _this.vinIsVaild = false;
          _this.vinSuccess = false;
          _this.step2Button = true;
          _this.showTip = false;
          _this.step1 = false;
          _this.step2 = true;
        } else {
          window.location.reload();
        }
      });
    },
    clickStep2: function clickStep2() {
      var _this2 = this;

      httpClient.get('/api/v1/vehicle/detail/vin?vin=' + this.vin, {}).then(function (response) {
        _this2.data2 = response.data.data;
        _this2.data2.mac = _this2.deviceId;

        if (_this2.data2.deviceMac == '') {
          _this2.isClickStep2 = true;
          _this2.vinSuccess = false;
        } else {}
      });
    },
    showStep3: function showStep3(data) {
      this.step1 = false;
      this.step2 = false;
      this.step3 = true;
      this.vehicleId = data.vehicleId;
    },
    clickStep1: function clickStep1() {
      var _this3 = this;

      httpClient.post('/api/v1/device/vehiceldetail/mac', {
        deviceMac: this.deviceId
      }).then(function (response) {
        _this3.data1 = response.data.data;

        if (_this3.data1.vin == '') {
          _this3.step1 = false;
          _this3.step2 = true;
        } else {
          _this3.vin = response.data.data.vin;
          _this3.title = "This device is currently paired with ".concat(response.data.data.year, " ").concat(response.data.data.make, " ").concat(response.data.data.model, " vin:").concat(response.data.data.vin);
          _this3.showTip = true;
        }
      }).catch(function (err) {
        console.log(err);
        _this3.vin = err.response.data.data.vin;
        _this3.data1 = err.response.data.data;
        _this3.title = "This device is currently paired with ".concat(_this3.data1.year, " ").concat(_this3.data1.make, " ").concat(_this3.data1.model, " vin:").concat(_this3.data1.vin);
        _this3.showTip = true;
      });
    }
  }
};
