import httpClient from '@/utils/httpclient';
import Alert from '@components/model';
import registerStatus from '@components/registStatus';
export default {
  name: 'userInviteRegister',
  components: {
    Alert: Alert,
    registerStatus: registerStatus
  },
  data: function data() {
    return {
      firstName: '',
      lastName: '',
      setPassWord: '',
      confirmPassWord: '',
      phone: '',
      status: 'accepted',
      showResult: false,
      showAlerts: {
        title: '',
        show: false,
        content: '',
        buttonText: ''
      },
      user: {
        name: '',
        lot: '',
        email: ''
      },
      btnGroup: {
        btnFirst: 'Login',
        btnSecond: 'Homepage'
      },
      errInfo: '',
      succInfo: '',
      icon: '',
      valueLength: ''
    };
  },
  mounted: function mounted() {
    var _this = this;

    var token = this.GetRequest().token; //let token = 'eyJhbGciOiJIUzI1NiJ9.eyJ0ZW5hbnRJZCI6ImJkMzM1MGJhMWRmZDQ4Njg5MTc5MTE3YWExNTY5ZTk5IiwiZW1haWwiOiJ3ZWltaW5Abm9uZGEudXMiLCJzdWIiOiJiZDMzNTBiYTFkZmQ0ODY4OTE3OTExN2FhMTU2OWU5OV93ZWltaW5Abm9uZGEudXMiLCJleHAiOjE1NjI4Mzc0ODN9.DDntzh__-hj25PHzpazZCm9iUyFR-yv2VPUdbgmBQpU';

    this.inviteRegister(token).then(function (res) {
      if (res.data.code === 200) {
        var data = res.data.data;
        _this.user.name = data.firstName + ' ' + data.lastName;
        _this.user.lot = data.storeName;
        _this.user.email = data.inviteEmail;
      } else if (res.data.code === 400) {
        var passData = {
          title: 'Register',
          show: true,
          content: 'Verify Failed!',
          buttonText: {
            btnFirst: '',
            btnSecond: 'OK'
          }
        };

        _this.closeRemind(passData);
      }
    }).catch(function (err) {
      console.log(err);
    });
  },
  computed: {
    firstNameState: function firstNameState() {
      return this.firstName.length > 0;
    },
    lastNameState: function lastNameState() {
      return this.lastName.length > 0;
    },
    setPassWordState: function setPassWordState() {
      return this.setPassWord.length > 5;
    },
    confirmPassWordState: function confirmPassWordState() {
      return this.confirmPassWord.length > 5;
    },
    phoneState: function phoneState() {
      return this.phone.length > 7;
    }
  },
  methods: {
    inviteRegister: function inviteRegister(token) {
      return httpClient.post('/api/v1/user/inviteLinkVerification', {
        token: token
      });
    },
    GetRequest: function GetRequest() {
      var url = location.search; //获取url中"?"符后的字串

      var theRequest = {};

      if (url.indexOf("?") !== -1) {
        var str = url.substr(1);
        var strs = str.split("&");

        for (var i = 0; i < strs.length; i++) {
          theRequest[strs[i].split("=")[0]] = strs[i].split("=")[1];
        }
      }

      return theRequest;
    },
    saveInviteData: function saveInviteData() {
      this.checkThePassWord(this.postData);
    },
    checkThePassWord: function checkThePassWord(cb) {
      //检验两次密码，是否勾选
      if (this.firstName === '' || this.lastName === '' || this.setPassWord === '' || this.confirmPassWord === '' || this.phone === '') {
        var passData = {
          title: 'Register',
          show: true,
          content: 'Please Fill In All Info!',
          buttonText: {
            btnFirst: '',
            btnSecond: 'OK'
          }
        };
        this.closeRemind(passData);
      } else if (this.confirmPassWord !== this.setPassWord) {
        var _passData = {
          title: 'Register',
          show: true,
          content: 'Password confirm error',
          buttonText: {
            btnFirst: '',
            btnSecond: 'OK'
          }
        };
        this.closeRemind(_passData);
        this.confirmPassWord = '';
      } else if (this.status !== 'accepted') {
        var _passData2 = {
          title: 'Register',
          show: true,
          content: 'Please Check Our Privacy Policy and Terms of Service',
          buttonText: {
            btnFirst: '',
            btnSecond: 'OK'
          }
        };
        this.closeRemind(_passData2);
      } else {
        cb();
      }
    },
    postData: function postData() {
      var _this2 = this;

      var data = {
        firstName: this.firstName,
        lastName: this.lastName,
        password: this.confirmPassWord,
        phone: this.phone,
        userEmail: this.user.email
      };
      var token = this.GetRequest().token;
      var link = '/api/v1/user/reg?token=' + token;
      httpClient.post(link, data).then(function (res) {
        if (res && res.data.code === 400) {
          _this2.showResult = !_this2.showResult;
          _this2.errInfo = 'Sorry , you have already been the user of ZUS.ai';
          _this2.succInfo = '';
          _this2.icon = 'fa fa-close';
        } else {
          _this2.showResult = !_this2.showResult;
          _this2.errInfo = '';
          _this2.succInfo = 'Congratulations, you have joined the store: Auto 3 Group. Log in or download the App to use the functions.';
          _this2.icon = 'cui cui-check';
        }
      });
    },
    closeRemind: function closeRemind(data) {
      this.showAlerts.show = data.show;
      this.showAlerts.title = data.title;
      this.showAlerts.content = data.content;
      this.showAlerts.buttonText = data.buttonText;
    },
    closeBox: function closeBox() {
      //子组件来请求父组件关闭弹窗
      this.showAlerts.show = !this.showAlerts.show;
    }
  }
};
