import httpClient from '@/utils/httpclient';
import Alert from '@components/model';
export default {
  name: 'adminInvite',
  components: {
    Alert: Alert
  },
  data: function data() {
    return {
      emails: [{
        name: ''
      }, {
        name: ''
      }],
      btnActive: true,
      submitSuccess: false,
      showAlerts: {
        title: '',
        content: '',
        show: false,
        buttonText: ''
      },
      storeName: ''
    };
  },
  mounted: function mounted() {
    var _this = this;

    httpClient.post('/api/v1/user/store').then(function (res) {
      _this.storeName = res.data.data.storeName;
    });
  },
  methods: {
    addInputBox: function addInputBox() {
      this.emails.push({
        name: ''
      });
    },
    getAllEmails: function getAllEmails() {
      var _this2 = this;

      var emailsArray = [];
      var url = '/api/v1/inviteUser';
      this.emails.map(function (item, i) {
        item.name.length ? emailsArray.push(item.name) : '';
      });
      httpClient.post(url, {
        emails: emailsArray.join(',')
      }).then(function (res) {
        if (res.data.code === 200) {
          _this2.submitSuccess = !_this2.submitSuccess;
        }
      });
    },
    verifyEmail: function verifyEmail(m) {
      //验证输入值，合法才可以提交
      var reg = /^.*@\w+(\.\w+)+/;

      if (!reg.test(m)) {
        var passData = {
          title: this.$t('m.tip.add_user_invite'),
          show: true,
          content: this.$t('m.tip.add_user_invalid_email'),
          buttonText: {
            btnFirst: '',
            btnSecond: this.$t('m.tip.b_ok'),
            userId: ''
          }
        };
        this.closeRemind(passData);
      } else {
        this.btnActive = false;
      }
    },
    closeBox: function closeBox() {
      this.showAlerts.show = !this.showAlerts.show;
    },
    continueInvite: function continueInvite() {
      this.submitSuccess = !this.submitSuccess; //点击continue，切换显示，清空之前的输入框值

      this.btnActive = !this.btnActive; //返回之后将按钮禁止

      this.emails.map(function (item, i) {
        item.name = '';
      });
    },
    closeRemind: function closeRemind(data) {
      this.showAlerts.show = data.show;
      this.showAlerts.title = data.title;
      this.showAlerts.content = data.content;
      this.showAlerts.buttonText = data.buttonText;
    }
  }
};
