import { wgs84ToGcjo2 } from "@/utils/coordtransform";
import AMap from 'AMap';
export default {
  name: "amap",
  props: ["map"],
  data: function data() {
    return {
      mapObj: null,
      polygon: null,
      lat: "",
      lng: "",
      safeLayer: null,
      roadLayer: null,
      mapInfo: null
    };
  },
  watch: {
    map: function map(newVal) {
      this.mapInfo = newVal;
      this.initMap();
    }
  },
  methods: {
    changeMapType: function changeMapType(type) {
      if (type == 'default') {
        this.roadLayer.hide();
        this.safeLayer.hide();
      } else {
        this.roadLayer.show();
        this.safeLayer.show();
      }
    },
    initMap: function initMap() {
      this.mapObj = new AMap.Map('polygon', {
        center: [this.mapInfo.center.lng, this.mapInfo.center.lat],
        zoom: 17
      });
      this.safeLayer = new AMap.TileLayer.Satellite({});
      this.roadLayer = new AMap.TileLayer.RoadNet({});
      this.mapObj.add(this.safeLayer);
      this.mapObj.add(this.roadLayer);
      var area2 = [];
      this.mapInfo['area'] ? this.mapInfo['area'].map(function (item) {
        area2.push(wgs84ToGcjo2(Number(item.lng), Number(item.lat)));
      }) : '';
      var polygon = new AMap.Polygon({
        path: area2,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '',
        fillOpacity: 0.35
      });
      this.mapObj.add(polygon);
      this.mapObj.setFitView();
    }
  }
};
