export function formatIdDate(str) {
  if (str.length === 8) {
    //8位数的转换
    var a = str.slice(0, 4);
    var b = str.slice(4, 6);
    var c = str.slice(6, 8);
    return a + '/' + b + '/' + c;
  } else if (str.length === 17) {
    //带有区间的17位数的转换
    var _a = str.slice(0, 4);

    var _b = str.slice(4, 6);

    var _c = str.slice(6, 8);

    var d = str.slice(9, 13);
    var e = str.slice(13, 15);
    var f = str.slice(15, 17);
    return _a + '/' + _b + '/' + _c + ' - ' + d + '/' + e + '/' + f;
  }
}
export function calAge(birthday) {
  var birYear = birthday.slice(0, 4);
  var year = new Date().getFullYear();
  return year - birYear;
}
export function calDriveAge(date) {
  var d1 = new Date(date.slice(0, 4), date.slice(4, 5), date.slice(5, 6)).getTime();
  var d2 = new Date().getTime();
  var diff = d2 - d1;
  var year = Math.floor(diff / (24 * 3600 * 1000 * 365));
  var month = Math.floor(diff - year / (1000 * 3600 * 24 * 30)) % 12;
  return {
    year: year,
    month: month
  };
}
