import httpclient from '@/utils/httpclient';
import myMap from "@/components/mapForAddress";
import model from "@/components/model";
export default {
  name: "shopDetail",
  components: {
    myMap: myMap,
    model: model
  },
  data: function data() {
    return {
      address: "",
      data: {},
      showTip: false,
      title: '',
      content: '',
      curDel: null,
      btnGroup: {},
      times: 0
    };
  },
  mounted: function mounted() {
    var _this = this;

    if (this.$route.params.id) {
      httpclient.get("/api/v1/store/detail?storeId=" + this.$route.params.id, {}).then(function (res) {
        if (res.data.code == 200) {
          _this.data = res.data.data;
        }
      });
    }
  },
  methods: {
    closeBox: function closeBox(type) {
      var _this2 = this;

      if (type) {
        this.times = this.times + 1;
        this.title = this.$t('m.tip.warn');
        this.content = this.$t('m.detail_shop.del2');

        if (this.times == 2) {
          httpclient.post("/api/v1/store/delete", {
            storeId: this.curDel
          }).then(function (res) {
            if (res.data.code == 200 && res.data.data.result) {
              _this2.$router.push("/shop-manager");
            } else {
              _this2.showTip = true;
              _this2.title = _this2.$t('m.tip.warn');
              _this2.content = _this2.$t('m.edit_shop.no_delete');
              _this2.btnGroup = {
                btnFirst: _this2.$t('m.tip.b_cancel')
              };
            }
          });
        }
      } else {
        this.showTip = false;
      }
    },
    removeData: function removeData(id) {
      this.times = 0;
      this.curDel = id;
      this.showTip = true;
      this.title = this.$t('m.tip.warn');
      this.content = this.$t('m.detail_shop.del1');
      this.btnGroup = {
        btnFirst: this.$t('m.tip.b_cancel'),
        btnSecond: this.$t('m.tip.b_ok')
      };
    }
  }
};
