/**
 * 时间戳转换为年月日时分秒日期字符串
 * @param time
 * @returns {string}
 */
export function getDateString(time) {
  var date = new Date(time);
  var Y = date.getFullYear() + ' ';
  var M = date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1;
  var D = date.getDate() < 10 ? '0' + date.getDate() : date.getDate();
  var h = date.getHours() < 10 ? '0' + date.getHours() : date.getHours();
  var m = date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes();
  return M + '/' + D + '/' + Y + ' ' + h + ':' + m + ' ';
}
/**
 * 时间戳转换为年月日字符串
 * @param time
 * @returns {string}
 */

export function getYMDString(time) {
  var date = new Date(time);
  var Y = date.getFullYear();
  var M = date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1;
  var D = date.getDate() < 10 ? '0' + date.getDate() + ' ' : date.getDate();
  return Y + '-' + M + '-' + D;
}
/**
 * 将日期字符串转换为时间戳
 * @param strtime
 * @returns {number}
 */

export function convertDateToTime(strtime) {
  // strtime.replace(/-/g, '/')
  var date = new Date(strtime);
  return date.getTime();
}
/**
 * 获取两个日期之间的天数
 * @param time1
 * @param time2
 * @returns {number}
 */

export function getDaysBetweenDate(time1, time2) {
  return Math.ceil((time2 - time1) / 1000 / 3600 / 24);
}
export function getYMDFormart(date) {
  var dt = new Date(date);
  var m = new Array("Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Spt", "Oct", "Nov", "Dec");
  var w = new Array("Sun", "Mon", "Tus", "Wed", "Thu", "Fri", "Sat");
  var mn = dt.getMonth();
  var wn = dt.getDay();
  var dn = dt.getDate();
  return w[wn] + " , " + m[mn] + " " + dn + " , " + dt.getFullYear();
}
