/**
 * 动态加载外部js插件
 * 入参: src(插件url)
 */

export function loadJs(src) {
    return new Promise(((resolve, reject) => {
        let script = document.createElement("script");
        script.type = "text/javascript";
        script.src = src;
        document.getElementsByTagName('body')[0].appendChild(script);
        script.onload = () => {
            resolve();
        };
        script.onerror = () => {
            reject();
        };
    }))
}
