    insertVehicleData: function insertVehicleData() {
      var _this = this;

      if (this.vehicleInsertId && this.vehicleColor && this.vehicleType && this.vehicleType != 0) {
        httpclient.post("/api/v1/clouddrive/vehicle/insert", {
          carchainVehicleId: this.vehicleInsertId,
          color: this.vehicleColor,
          vehicleType: this.vehicleType
        }).then(function (res) {
          if (res.data.code == 200 && res.data.data.result) {
            _this.insertStatus = _this.$t("m.cloud.insert_succ_title");
            _this.insertTipContent = _this.$t("m.cloud.insert_succ_content");
            _this.insertTip = true;
          } else {
            alert(res.data.data.content);
          }
        });
      } else {
        alert("请检查输入参数");
      }
    },
    letChildGetMap: function letChildGetMap() {
      var _this2 = this;

      //让子组件请求接口展示多点地图
      var _search = [];

      if (this.selected_parking) {
        _search.push({
          key: "parking",
          value: [this.selected_parking]
        });
      }

      if (this.selected_battery) {
        _search.push({
          key: "battery",
          value: [this.selected_battery]
        });
      }

      if (this.selected_offline) {
        _search.push({
          key: "online",
          value: [this.selected_offline]
        });
      }

      if (this.selected_drive) {
        _search.push({
          key: "inUse",
          value: [this.selected_drive]
        });
      }

      var link = "/api/v1/vehiclesearch/searchnewmap?searchText=" + this.search + "&source=&pageNo=1&pageSize=" + this.countSize;
      httpclient.post(link, {
        filters: _search
      }).then(function (res) {
        _this2.searchGaodeMap = res.data.data;
        _this2.searchGoogleMap = res.data.data;
      });
