      var _this3 = this;

      httpclient.post("/api/v1/vehiclesearch/filter").then(function (response) {
        _this3.gFData = response.data.data;
        var _data = response.data.data;
        var parking_arr = [];

        _data[0].filters.map(function (item) {
          parking_arr.push({
