    axiosApi: function axiosApi(self, page, obj) {
      var _this4 = this;

      httpclient.post("/api/v1/vehiclesearch/searchnew?searchText=".concat(this.search, "&source=&pageNo=").concat(page, "&pageSize=10"), {
        filters: obj
      }).then(function (response) {
        self.data = response.data.data;

        if (self.data) {
          self.total = response.data.data.rowCount ? response.data.data.rowCount : 0;
          self.currentPage = response.data.data.pageNo ? response.data.data.pageNo : 0;

          _this4.filterData(self.data.vehicles);
        } else {
          self.total = 0;
          self.currentPage = 0;

          _this4.filterData([]);
        }
      }).catch(function (err) {
        alert(err);
      });
    },
    formatNumber: function formatNumber(nStr) {
      nStr += "";
      var x = nStr.split(".");
      var x1 = x[0];
      var x2 = x.length > 1 ? "." + x[1] : "";
      var rgx = /(\d+)(\d{3})/;

      while (rgx.test(x1)) {
        x1 = x1.replace(rgx, "$1" + "," + "$2");
      }

      return x1 + x2;
    },
    changePage: function changePage(page) {
      var self = this;
      this.axiosApi(self, page, []);
    },
    apiDel: function apiDel() {
      var _this5 = this;

      httpclient.post("/api/v1/vehicle/delete", {
        vehicleId: this.delId
      }).then(function (response) {
        if (response.data.data.errCode == 100) {
          _this5.reload();
        } else {
          _this5.delErrContent = _this5.delErrorMsg[response.data.data.errCode];
          _this5.showDelError = true;
        }

        _this5.showDelMap = false;
      }).catch(function () {
        _this5.delErrContent = _this5.$t("m.err_message.vehicle_param_error");
        _this5.showDelError = true;
      });
    },
    showMapTip: function showMapTip(lat, lng, area, info) {
      if (lat || area.length) {
        this.map = {
          lat: lat,
          lng: lng,
          area: area,
          vehicleInfo: info
        };
        this.showMap = true;
      }
    },
    format_number: function format_number(nStr) {
      nStr += "";
      var x = nStr.split(".");
      var x1 = x[0];
      var x2 = x.length > 1 ? "." + x[1] : "";
      var rgx = /(\d+)(\d{3})/;

      while (rgx.test(x1)) {
        x1 = x1.replace(rgx, "$1" + "," + "$2");
      }

      return x1 + x2;
    },
    hideModal: function hideModal() {
