import httpClient from "@/utils/httpclient";
import { getDateString } from "@/utils/date";
export default {
  name: "shopManager",
  inject: ['reload'],
  data: function data() {
    return {
      tableFields: {
        orderId: {
          label: this.$t('m.cloud.order_id'),
          class: "text-center"
        },
        userInfo: {
          label: this.$t('m.cloud.user_info'),
          class: "text-center"
        },
        vehicleInfo: {
          label: this.$t('m.cloud.vehicle_info'),
          class: "text-center"
        },
        testInfo: {
          label: this.$t('m.dashboard.test_point'),
          class: "text-center"
        },
        pointTime: {
          label: this.$t('m.cloud.rese_test_drive'),
          class: "text-center"
        },
        status: {
          label: this.$t('m.cloud.status'),
          class: "text-center"
        },
        question: {
          label: this.$t('m.cloud.check_question'),
          class: "text-center"
        },
        actions: {
          label: this.$t('m.vehicle.actions'),
          class: "Actions"
        }
      },
      table: null,
      currentPage: 1,
      pageSize: 10,
      total: 0,
      totalPages: 0,
      showTip: false,
      curName: '',
      statusList: [],
      cancelTip: false,
      curOrder: null,
      curOrderNum: null,
      tipStatus: false,
      canStatus: {
        title: "",
        content: ""
      },
      curStatus: "all"
    };
  },
  mounted: function mounted() {
    var _this = this;

    httpClient.get('/api/v1/clouddrive/order/orderstatuslist').then(function (res) {
      if (res.data.code == 200) {
        _this.statusList = res.data.data.statusList;

        _this.statusList.map(function (item) {
          switch (item.orderCode) {
            case 10:
              item['content'] = _this.$t('m.cloud.wait_pick_up');
              break;

            case 20:
              item['content'] = _this.$t('m.cloud.wait_return');
              break;

            case 30:
              item['content'] = _this.$t('m.cloud.returned_vehicle');
              break;

            case 40:
              item['content'] = _this.$t('m.cloud.complete');
              break;

            case 50:
              item['content'] = _this.$t('m.cloud.canceled');
              break;
          }
        });
      }
    });
    this.initData(this.currentPage);
  },
  methods: {
    searchData: function searchData() {
      this.currentPage = 1;
      this.initData(1);
    },
    initData: function initData(page) {
      var _this2 = this;

      var orderStatus = !this.curStatus || this.curStatus == 'all' ? '' : "&orderStatus=".concat(this.curStatus);
      httpClient.get("/api/v1/clouddrive/order/pagelist?pageNo=".concat(page, "&pageSize=").concat(this.pageSize).concat(orderStatus)).then(function (response) {
        if (response.data.code == 200) {
          _this2.total = response.data.data.rowCount;
          _this2.data = response.data.data;
          _this2.pageCount = response.data.data.totalPages;

          _this2.filterData();
        }
      });
    },
    acceptVehicle: function acceptVehicle(id) {
      httpClient.get('/api/v1/clouddrive/order/check?orderId=' + id).then(function (res) {
        if (res.data.code == 200 && res.data.data.result) {
          window.location.reload();
        } else {
          alert("车辆异常");
        }
      });
    },
    filterData: function filterData() {
      this.table = this.data.datas.map(function (item) {
        return {
          orderId: item.orderServiceNum,
          id: item.orderId,
          username: item.userName,
          phone: item.phone,
          year: item.vehicleYear,
          make: item.vehicleMake,
          model: item.vehicleModel,
          color: item.vehicleColor,
          alias: item.vehicleAlias,
          trim: item.vehicleTrim,
          plat: item.vehiclePlate,
          vehicleId: item.vehicleId,
          userId: item.userId,
          num: item.vehiclePlate,
          // 库存编号
          testInfo: item.parkingName,
          pointTime: getDateString(item.appointmentStartTime) + "~" + getDateString(item.appointmentEndTime).split("  ")[1],
          status: item.orderStatus,
          question: item.questionnaireStatus
        };
      });
    },
    openOrderTip: function openOrderTip(id, orderId) {
      this.curOrder = id;
      this.curOrderNum = orderId;
      this.cancelTip = true;
    },
    cancelOrder: function cancelOrder() {
      var _this3 = this;

      httpClient.post("/api/v1/clouddrive/order/cancel", {
        orderId: this.curOrder
      }).then(function (response) {
        if (response.data.code == 200 && response.data.data.result) {
          _this3.canStatus = {
            title: _this3.$t('m.cloud.can_order_suc'),
            content: _this3.$t('m.cloud.can_order_suc_c', {
              order: _this3.curOrderNum
            })
          };

          _this3.reload();
        } else {
          _this3.canStatus = {
            title: _this3.$t('m.cloud.can_order_fail'),
            content: _this3.$t('m.cloud.can_order_fail_c')
          };
        }

        _this3.tipStatus = true;
      });
    },
    prevPage: function prevPage() {
      this.initData(this.currentPage > 1 ? this.currentPage - 1 : 1);
    },
    nextPage: function nextPage() {
      this.initData(this.currentPage >= this.pageCount ? this.currentPage : this.currentPage + 1);
    },
    changePage: function changePage(page) {
      this.initData(page);
    }
  }
};
