import google from "google";
export default {
  name: "googleMap",
  props: ["address"],
  data: function data() {
    return {
      addres: "",
      mapObj: null
    };
  },
  watch: {
    address: function address(newVal) {
      this.addres = newVal;
      this.initMap();
    }
  },
  methods: {
    initMap: function initMap() {
      var _this = this;

      this.mapObj = new google.maps.Map(document.getElementById('map'), {
        zoom: 15,
        mapTypeId: google.maps.MapTypeId.SATELLITE
      });
      this.geocoders = new google.maps.Geocoder();
      this.geocoders.geocode({
        'address': this.addres
      }, function (results, status) {
        if (status == "OK") {
          _this.mapObj.setCenter(results[0].geometry.location);

          new google.maps.Marker({
            map: _this.mapObj,
            position: results[0].geometry.location,
            icon: "https://cdn.shopify.com/s/files/1/0703/1149/files/60x60.png?854740"
          });
        } else {
          console.log('Geocode was not successful for the following reason: ' + status);
        }
      });
    }
  }
};
