import httpclient from "@/utils/httpclient";
import { getDateString as _getDateString, getDaysBetweenDate as _getDaysBetweenDate } from "@/utils/date";
import myMap from "@/components/map";
import Alert from "@components/model";
import "@/utils/viewer"; //该组件的js是预览图片组件，按需引用即可

export default {
  name: "vehicleDetail",
  components: {
    myMap: myMap,
    Alert: Alert
  },
  data: function data() {
    return {
      btnGroup: {
        btnFirst: "Cancel",
        btnSecond: "OK"
      },
      data: {},
      map: null,
      searchGoogleMap: {},
      mapPopup: null,
      tipTitle: "",
      showSoldVehicle: false,
      soldVehicleStatus: false,
      soldVehicleSuccess: false,
      soldVehicleTransform: false,
      installDeviceStatus: false,
      installSuccess: false,
      installStatus: false,
      inputText: "",
      soldVehicleContent: "",
      soldVehicleHeader: "",
      transferStatus: false,
      transferContents: "",
      transferTitle: "",
      soldStatus: "",
      soldOutC: "",
      fields: {
        operation: {
          key: "operationType",
          label: this.$t("m.common.action")
        },
        vehicle: {
          key: "vehicle",
          label: this.$t("m.vehicle_detail.operator")
        },
        time: {
          key: "time",
          label: this.$t("m.common.time")
        }
      },
      options: [],
      selected: null,
      items: [],
      totalPages: 1,
      currentPage: 1,
      perPage: 10,
      showDelMap: false,
      rows: 1,
      myCurrentMileage: null,
      imeiIsVaild: false,
      bUnpair: false,
      showAlerts: {
        title: "",
        show: false,
        content: "",
        buttonText: ""
      },
      deviceMac: "",
      vin: "",
      isOnline: ""
    };
  },
  watch: {
    inputText: function inputText(newV) {
      if (newV.length === 15) {
        this.imeiIsVaild = true;
      } else {
        this.imeiIsVaild = false;
      }
    }
  },
  mounted: function mounted() {
    var _this = this;

    if (this.$i18n.locale === "en-US") {
      this.changedLanguage = "English";
    } else {
      this.changedLanguage = "Chinese";
    }

    var self = this;
    httpclient.get("/api/v1/vehicle/detail/id?vehicleId=" + this.$route.params.id, {}).then(function (response) {
      self.data = response.data.data;
      _this.isOnline = response.data.data.isOnline;
      _this.deviceMac = response.data.data.deviceMac;
      _this.vin = response.data.data.vin;
      var year = self.data.year ? self.data.year : "N/A";
      var make = self.data.make ? self.data.make : "N/A";
      var model = self.data.model ? self.data.model : "N/A";
      var info = year == "N/A" && make == "N/A" && model == "N/A" ? "N/A" : year + " " + make + " " + model;
      self.tipTitle = info;
      self.map = {
        lat: self.data.lastLocationLat ? self.data.lastLocationLat : 0,
        lng: self.data.lastLocationLng ? self.data.lastLocationLng : 0,
        area: self.data.areaCoordinates ? self.data.areaCoordinates : [],
        vehicleInfo: info
      };
      self.mapPopup = {
        lat: self.data.lastLocationLat ? self.data.lastLocationLat : 0,
        lng: self.data.lastLocationLng ? self.data.lastLocationLng : 0,
        area: self.data.areaCoordinates ? self.data.areaCoordinates : [],
        vehicleInfo: info
      };
      var _mileage = null;

      if (response.data.data.currentMileage) {
        if (self.changedLanguage === "English") {
          _mileage = (response.data.data.currentMileage * 0.0006214).toFixed(0);
        } else {
          _mileage = (response.data.data.currentMileage / 1000).toFixed(0);
        }
      } else {
        _mileage = "N/A";
      }

      self.myCurrentMileage = _mileage;
    }).catch(function (err) {
      // console.dir(err);
      err;
    });
    this.getVehicleOperatorList(1);
  },
  methods: {
    reloadPage: function reloadPage() {
      window.location.reload();
    },
    getDateString: function getDateString(data) {
      return _getDateString(data);
    },
    getDaysBetweenDate: function getDaysBetweenDate(data, time) {
      return _getDaysBetweenDate(data, time);
    },
    getVehicleOperatorList: function getVehicleOperatorList(page) {
      var _this2 = this;

      var myVehicleId = this.$route.params.id;
      var myLink = "/api/v1/operationlog/listbyvehicleid?pageNo=" + page + "&vehicleId=" + myVehicleId;
      httpclient.get(myLink).then(function (res) {
        var myData = res.data.data.datas;
        _this2.items = myData;
        _this2.searchGoogleMap = myData; // this.currentPage = res.data.data.pageNo;

        _this2.perPage = res.data.data.pageSize;
        _this2.totalPages = res.data.data.totalPages;
        _this2.rows = res.data.data.rowCount;
        var myArr = [];
        myData.map(function (item) {
          myArr.push({
            operationType: item.operationType,
            operator: item.userName,
            time: _this2.getDateString(item.createTime),
            userId: item.userId
          });
        });
        _this2.items = myArr;
      });
    },
    turnToPage: function turnToPage(p) {
      this.getVehicleOperatorList(p ? p : 1);
    },
    showRegisterSold: function showRegisterSold() {
      this.soldOutC = this.$t("m.sold_vehicle.sold_content", {
        year: this.data.year ? this.data.year : "N/A",
        make: this.data.make ? this.data.make : "N/A",
        model: this.data.model ? this.data.model : "N/A",
        alias: this.data.alias ? this.data.alias : "N/A",
        stock: this.data.stockNumber ? this.data.stockNumber : "N/A"
      });
      this.showSoldVehicle = true;
    },
    delData: function delData() {
      this.showDelMap = true;
    },
    apiDel: function apiDel() {
      var _this3 = this;

      //删除车辆
      httpclient.post("/api/v1/vehicle/delete", {
        vehicleId: this.$route.params.id
      }).then(function (response) {
        if (response.data.code == 200) {
          _this3.showDelMap = !_this3.showDelMap; // this.reload();

          _this3.$router.push("/inventory/vehicle/list");
        }
      });
    },
    registerSold: function registerSold() {
      var _this4 = this;

      //确认登记售出
      var vehicleId = this.$route.params.id;
      httpclient.post("/api/v1/vehicle/sale", {
        vehicleId: vehicleId
      }).then(function (res) {
        if (res.data.code === 200 && res.data.data.result) {
          _this4.soldVehicleSuccess = true;
          _this4.soldVehicleStatus = true;
          _this4.data.soldVehicleContent = _this4.$t("m.sold_vehicle.sold_content_success", {
            year: _this4.data.year ? _this4.data.year : "N/A",
            make: _this4.data.make ? _this4.data.make : "N/A",
            model: _this4.data.model ? _this4.data.model : "N/A",
            alias: _this4.data.alias ? _this4.data.alias : "N/A",
            stock: _this4.data.stockNumber ? _this4.data.stockNumber : "N/A"
          });
          _this4.data.soldVehicleHeader = _this4.$t("m.sold_vehicle.sold_header_success");
        } else {
          _this4.soldVehicleSuccess = false;
          _this4.soldVehicleStatus = true;
          _this4.data.soldVehicleContent = _this4.$t("m.sold_vehicle.sold_content_fail", {
            year: _this4.data.year ? _this4.data.year : "N/A",
            make: _this4.data.make ? _this4.data.make : "N/A",
            model: _this4.data.model ? _this4.data.model : "N/A",
            alias: _this4.data.alias ? _this4.data.alias : "N/A",
            stock: _this4.data.stockNumber ? _this4.data.stockNumber : "N/A"
          });
          _this4.data.soldVehicleHeader = _this4.$t("m.sold_vehicle.sold_header_fail");
        }
      });
    },
    goBackToList: function goBackToList() {
      //回到列表
      if (this.soldVehicleSuccess) {
        this.$router.push("/inventory/vehicle/list");
      }
    },
    confirmTransformVehicle: function confirmTransformVehicle() {
      var _this5 = this;

      //车辆转移确认
      if (this.selected !== null) {
        var _name = "";
        this.options.map(function (item) {
          if (item.value === _this5.selected) {
            _name = item.text;
          }
        });
        var data = {
          vehicleId: this.$route.params.id,
          transferStoreId: this.selected
        };
        httpclient.post("/api/v1/vehicle/transfer", data).then(function (res) {
          _this5.transferStatus = true;

          if (res.data.code === 200 && res.data.data.result === true) {
            _this5.transferTitle = _this5.$t("m.sold_vehicle.transform_vehicle_to_other_store_header_success");
            _this5.transferContents = _this5.$t("m.sold_vehicle.transform_vehicle_to_other_store_content_success", {
              year: _this5.data.year ? _this5.data.year : "N/A",
              make: _this5.data.make ? _this5.data.make : "N/A",
              model: _this5.data.model ? _this5.data.model : "N/A",
              alias: _this5.data.alias ? _this5.data.alias : "N/A",
              storeName: _name,
              stock: _this5.data.stockNumber ? _this5.data.stockNumber : "N/A"
            });
          } else {
            _this5.transferTitle = _this5.$t("m.sold_vehicle.transform_vehicle_to_other_store_header_fail");
            _this5.transferContents = _this5.$t("m.sold_vehicle.transform_vehicle_to_other_store_content_fail");
          }
        });
      }
    },
    showTransformVehicle: function showTransformVehicle() {
      var _this6 = this;

      //弹出车辆转移的弹窗，获取门店列表
      this.soldVehicleTransform = true;
      httpclient.get("/api/v1/store/listbyself?vehicleid=" + this.$route.params.id).then(function (res) {
        if (res.data.code === 200 && res.data.data.length) {
          _this6.options.length = 0;

          _this6.options.push({
            value: null,
            text: _this6.$t("m.sold_vehicle.transform_vehicle_to_other_store_options"),
            disabled: true
          });

          res.data.data.map(function (item) {
            _this6.options.push({
              value: item.storeId,
              text: item.storeName
            });
          });
        }
      });
    },
    afterTransfer: function afterTransfer() {
      //转移成功，跳转到vehicle-list
      if (this.transferStatus) {
        this.$router.push("/inventory/vehicle/list");
      }
    },
    hideModal: function hideModal() {
      this.showDelMap = !this.showDelMap;
    },
    showBindInfo: function showBindInfo() {
      this.installDeviceStatus = !this.installDeviceStatus;
    },
    installDevice: function installDevice() {
      var _this7 = this;

      httpclient.post("/api/v1/clouddrive/device/box/bind", {
        deviceImei: this.inputText,
        vehicleId: this.$route.params.id,
        deviceType: 1
      }).then(function (res) {
        if (res.data.code === 200 && res.data.data.result) {
          _this7.installStatus = true;
          _this7.installSuccess = true; // window.location.reload();
        } else {
          _this7.installStatus = false;
          _this7.installSuccess = true;
        }
      });
    },
    unPairdevice: function unPairdevice() {
      //设备解绑，未绑定的设备，按钮不显示
      var link = "/api/v1/vehicle/unbind/device";
      var self = this;
      var data = {
        deviceMac: this.deviceMac,
        vin: this.vin
      };
      httpclient.post(link, data).then(function (res) {
        if (res.data.code === 200) {
          var passData = {
            title: self.$t("m.tip.info"),
            show: true,
            content: self.$t("m.tip.vehicle_unpair_success"),
            buttonText: {
              btnFirst: "",
              btnSecond: self.$t("m.tip.b_ok")
            }
          };
          self.closeRemind(passData); // window.location.reload()
        }
      }).catch(function (err) {
        if (err) {
          var passData = {
            title: self.$t("m.tip.info"),
            show: true,
            content: self.$t("m.tip.vehicle_unpair_failed"),
            buttonText: {
              btnFirst: "",
              btnSecond: self.$t("m.tip.b_ok")
            }
          };
          self.closeRemind(passData);
        }
      });
    },
    closeRemind: function closeRemind(data) {
      this.showAlerts.show = data.show;
      this.showAlerts.title = data.title;
      this.showAlerts.content = data.content;
      this.showAlerts.buttonText = data.buttonText; // window.location.reload()
    },
    closeBox: function closeBox() {
      //子组件来请求父组件关闭弹窗
      this.showAlerts.show = !this.showAlerts.show;
      window.location.reload();

      if (this.updateDetail === "update") {
        //如果是update车辆信息，成功后到详情页面
        this.$router.push("/test-drive/test-vehicle/detail/" + this.id);
      }
    }
  }
};
