.vehicle-detail {
    .margin-b-10 {
        margin-bottom: 10px;
    }
    .margin-top-46 {
        margin-top: -46px;
    }
    .map-img-18 {
        height: 18px;
        width: 18px;
    }
    .select-store {
        .modal-dialog {
            max-width: 800px;
        }

        .select-box {
            display: flex;
            justify-content: space-between;
            align-items: center;

            .col-md-4 {
                padding-left: 0;
            }

            .col-md-8 {
                padding-right: 0;
            }

            p {
                margin-bottom: 0;
            }
        }
    }

    .row {
        border-bottom: 1px solid #23282c;
        padding: 0.75rem 1.25rem;

        .img-box {
            width: 100%;
        }
    }

    .gaode-fullscreen {
        background: #ffffff;
        width: 30px;
        height: 30px;
        text-align: center;
        line-height: 30px;
        position: absolute;
        z-index: 1;
        right: 6px;
        top: 6px;
    }

    .show-map .modal-dialog {
        width: 80%;
        max-width: 1200px;
    }

    .user-table-list {
        margin-top: 20px;

        .link-detail {
            color: #20a8d8 !important;
            cursor: pointer;

            &:hover {
                text-decoration: underline !important;
                color: #167495 !important;
            }
        }
    }
}

.vehicle-detail {
    .card-body {
        padding: 0 1.25rem;
    }

    .title {
        font-size: 18px;
        color: #63c2de;
    }

    .card {
        margin: 0 auto;
    }

    .hidden {
        display: none;
    }

    .left {
        padding: 10px;
        padding-top: 0;

        .row {
            margin: 0px;
            border-bottom: 1px solid #23282c;
            padding: 10px;
        }

        .row > div {
            padding-left: 4px;
        }
    }

    .right {
        .table thead th {
            color: grey;
        }

        #map {
            height: 40vh;
        }
    }
}

.modal-dialog {
    max-width: 50% !important;
    width: 50% !important;
}

.lock-my-car {
    .modal-header {
        .modal-title {
            width: 100%;
            text-align: center;
        }
    }

    .modal-footer {
        justify-content: center;
    }
}

.remind-modal {
    .modal-footer {
        .btn {
            margin: 0 auto;
        }
    }
}

.has-link-detail {
    cursor: pointer;
    font-weight: bolder;
    margin-bottom: 1px;
    color: #63c2de;
    text-decoration: underline;
}

.button-red {
    display: inline-block;
    width: 50px;
    height: 20px;
    background-color: #f86c6b !important;
    color: #fff;
    line-height: 10px;
    border-radius: 5px;
    border: none;
    padding: 0;
}

.button-primary {
    display: inline-block;
    width: 80px;
    height: 20px;
    line-height: 10px;
    background-color: #63c2de !important;
    color: #fff !important;
    border: none;
    border-radius: 5px;
}

.online-success {
    background: #4dbd74;
    padding: 0 10px;
    border-radius: 10px;
    font-weight: bold;
    color: #ffffff;
}

.online-danger {
    background-color: #f86c6b;
    padding: 0 10px;
    border-radius: 10px;
    font-weight: bold;
    color: #ffffff;
}

.online-gray {
    background-color: #c8ced3;
    padding: 0 10px;
    border-radius: 10px;
    color: #23282c;
    font-weight: bold;
}
