#toast-container {
    position: fixed;
    z-index: 999999;
    right: 24px;
    top: 10px;
    .toast-error {
        background-color: #bd362f;
    }

    .toast {
        span {
            font-size: 24px;
            position: absolute;
            left: 4%;
            top: 34%;
        }
        i {
            position: absolute;
            right: 5%;
            top: 35%;
            font-size: 15px;
            color: darkslategrey;
        }
        position: relative;
        pointer-events: auto;
        overflow: hidden;
        margin: 0 0 6px;
        padding: 15px 15px 15px 50px;
        width: 300px;
        -moz-border-radius: 3px;
        -webkit-border-radius: 3px;
        border-radius: 3px;
        background-position: 15px center;
        background-repeat: no-repeat;
        -moz-box-shadow: 0 0 12px #999;
        -webkit-box-shadow: 0 0 12px #999;
        box-shadow: 0 0 12px #999;
        color: #fff;
        opacity: .8;
        -ms-filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=80);
        filter: alpha(opacity=80);
    }
    .toast-title {
        font-weight: bold;
    }
    .toast-message {
        word-wrap: break-word;
        cursor: pointer;
    }
}
#toast-container * {
    box-sizing: border-box;
}
