let city = null;
import httpClient from "@/utils/httpclient";
import model from "@/components/model";
if (process.env.NODE_ENV == 'production') {
    city =  require("@/static/cityList.js");
} else {
    city = require("@/static/cityListDev.js");
}
export default {
    name: "register",
    components: {
        model
    },
    data() {
        return {
            show: false,
            btnGroup: {
                btnFirst: "Cancel",
                btnSecond:'OK'
            },
            content: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            confirmP: "",
            phone: "",
            dGroup: "",
            dName: "",
            country: "United States",
            state: "Alabama",
            city: "Abanda",
            check: true,
            cityList: city.cityList,
            street: "",
            apartment: "",
            zipCode: "",
            disable: true,
            firstE: false,
            lastE: false,
            passwordE: false,
            confirmpE: false,
            status: true,
            emailE: false,
            phoneE: false,
            dGroupE: false,
            dNameE: false,
            streetE: false,
            apartE: false,
            zipE: false
        }
    },
    watch: {
        state() {
            this.city = this.cityList[this.country][this.state][0]
        },
        firstName(newV) {
            this.firstName = newV;
            this.checkInput();
        },
        lastName(newV) {
            this.lastName = newV;
            this.checkInput();
        }
    },
    methods: {
        choiceRule() {
            this.check = !this.check;
            this.checkInput();
        },
        checkInput() {
            this.status = true;
            if (!this.firstName || !this.lastName || !this.email || !this.password ||
                !this.confirmP || !this.phone || !this.dGroup || !this.dName || !this.country
                || !this.state || !this.city || !this.check ) {
                this.disable = true;
                this.status = false;
            }
            !this.firstName ? this.firstE = true : this.firstE = false;
            !this.lastName ? this.lastE = true : this.lastE = false;
            !this.email || (this.email && this.email.indexOf("@") < 0) ? this.emailE = true : this.emailE = false;
            !this.phone || (this.phone && this.phone.length > 15) ? this.phoneE = true : this.phoneE = false;
            !this.dGroup ? this.dGroupE = true : this.dGroupE = false;
            !this.dName ? this.dNameE = true : this.dNameE = false;

            this.checkPassword();
            this.checkConfirmPa();

            if (this.status) {
                this.disable = false;
            }
        },
        checkPassword() {
            if (this.password && this.password.length < 6) {
                this.passwordE = true;
                this.status = false;
            } else {
                this.passwordE = false;
            }
        },
        checkConfirmPa() {
            if ( (this.confirmP && this.confirmP.length < 6) ||  this.password != this.confirmP) {
                this.confirmpE = true;
                this.status = false;
            } else {
                this.confirmpE = false;
            }
        },
        submitData() {
            this.checkInput();
            if (this.status) {
                httpClient.post('/api/v1/manager/reg', {
                    userEmail: this.email,
                    password: this.password,
                    firstName: this.firstName,
                    lastName: this.lastName,
                    phone: this.phone,
                    storeName: this.dName,
                    storeAddress: this.apartment,
                    tenantName: this.dGroup,
                    country: this.country,
                    state: this.state,
                    city: this.city,
                    streetAddress: this.street,
                    zipCode: this.zipCode
                }).then(response => {
                    if (response.data.data && response.data.data == "ok" && response.data.code == 200) {
                        this.$router.push("/register-success");
                    } else {
                        this.show = true;
                        this.content = response.data.msg;
                    }
                }).catch(error => {
                    window.location.reload();
                })
            }
        }
    }
}
