.searchGaode {
    #gaode-map {
        width: 100%;
        height: 65vh;
        margin-bottom: 5vh;
        .amap-info-contentContainer {
            width: 100%;
            border-radius: 10px;
            .amap-info-content{
                padding: 20px 10px 0;
            }
            .amap-info-close{
                right:10px !important;
            }
        }
        .amap-info-shadowContainer + div {
            bottom: 30px!important;
        }
    }
    .map-btn-group {
        position: absolute;
        margin-top: 10px;
        z-index: 1;
        margin-left: 20px;
        ul {
            color: grey;
            padding: 0 20px;
            height: 40px;
            line-height: 40px;
            background: #ffffff;
            font-size: 18px;
            li {
                cursor: pointer;
                display: inline-block;
            }
        }
    }
    .content-window-card {
        position: relative;
        box-shadow: none;
        bottom: 0;
        left: 0;
        width: auto;
        padding: 0;
    }

    .content-window-card p {
        height: 2rem;
    }

    .custom-info {
        border: solid 1px silver;
    }

    div.info-top {
        position: relative;
        background: none repeat scroll 0 0 #F9F9F9;
        border-bottom: 1px solid #CCC;
        border-radius: 5px 5px 0 0;
    }

    div.info-top div {
        display: inline-block;
        color: #333333;
        font-size: 14px;
        font-weight: bold;
        line-height: 31px;
        padding: 0 10px;
    }

    div.info-top img {
        position: absolute;
        top: 10px;
        right: 10px;
        transition-duration: 0.25s;
    }

    div.info-top img:hover {
        box-shadow: 0px 0px 5px #000;
    }

    div.info-middle {
        font-size: 12px;
        padding: 10px 6px;
        line-height: 20px;
    }

    div.info-bottom {
        height: 0px;
        width: 100%;
        clear: both;
        text-align: center;
    }

    div.info-bottom img {
        position: relative;
        z-index: 104;
    }

    span {
        margin-left: 5px;
        font-size: 11px;
    }

    .info-middle img {
        float: left;
        margin-right: 6px;
    }
}
