import httpClient from "@/utils/httpclient";
import succ from "@/components/registStatus";
export default {
    name: "registerCheckSuccess",
    components: {
        succ
    },
    data() {
        return {
            succInfo: "",
            errInfo: "",
            btnGroup: {
                btnFirst: "login"
            },
            status: ''
        }
    },
    mounted() {

        httpClient.post("/api/v1/email/verification", {
            token: this.$route.query.token
        }).then(response => {
            if (response.data.data && response.data.data == "ok") {
                this.succInfo = "Registration Complete!";
                this.status = 1;
                this.errInfo = null;
            } else {
                this.errInfo = "Sorry, the verification link has been used or has expired.";
                this.status = 2;
                this.succInfo = null;
            }
        }).catch(err => {
            this.errInfo = "Sorry, the verification link has been used or has expired.";
            this.status = 2;
            this.succInfo = null;
        })
    }
}
