	import httpClient from '@/utils/httpclient';
	import Alert from '@components/model'

	export default {
		name: 'adminEdit',
		components: {
			Alert
		},
		data() {
			return {
				id: this.$route.params.id,
				userName: '',
				firstName: '123',
				lastName: '432',
				userEmail: '82482@u8u28.com',
				phone: '',
        adminId:0,
				status: 1,
				role: '',
				options1: [
					'Dealership Staff', 'Dealership Manager', 'Dealership Group Administrator'
				],
				selected1: '',
				options2: [
					'Active', 'InActive'
				],
				selected2: '',
				showAlerts: {
					title: '',
					content: '',
					show: false,
					buttonText: ''
				}
			}
		},
		mounted() {
        this.adminId = this.$store.state.userInfo.userId;
			let url = 'https://api-saas-qa.zus.ai/api/v1/getUserById';
			httpClient.post(url, {userId: this.id}).then((res) => {
				let data = res.data.data;
				this.userName = data.lastName + ' ' + data.firstName;
				this.firstName = data.firstName;
				this.lastName = data.lastName;
				this.userEmail = data.userEmail;
				this.phone = data.phone;
				this.status = data.status === 1 ? 'Active' : 'InActive';
				this.role = data.role;
				//初始化role 和 status的下拉选择
				let i = this.options1.findIndex((val) => val === this.role);
				this.selected1 = this.options1[i];
				let j = this.options2.findIndex((item) => item === this.status);
				this.selected2 = this.options2[j]
			})
		},
		methods: {
			saveData() {
				let link = 'https://api-saas-qa.zus.ai/api/v1/updateUser';
				let userRole;
				if (this.selected1 === 'Dealership Staff') {
					userRole = 'user'
				} else if (this.selected1 === 'Dealership Manager') {
					userRole = 'manager'
				} else {
					userRole = 'root'
				}
				let data = {
					userId: this.id,
					role: userRole,
					status: this.selected2 === 'Active' ? 1 : 0
				};
				httpClient.post(link, data).then((res) => {
					if (res.data.code === 200) {
						let passData = {
							title: 'User Information Updated!',
							show: true,
							content: '',
							buttonText: {
								btnFirst: '',
								btnSecond: 'OK',
								userId: ''
							}
						};
						this.closeRemind(passData);
					}
				})
			},
			closeBox() {
				this.showAlerts.show = !this.showAlerts.show;
			},
			closeRemind(data) {
				this.showAlerts.show = data.show;
				this.showAlerts.title = data.title;
				this.showAlerts.content = data.content;
				this.showAlerts.buttonText = data.buttonText;
			},
		}
	}
