	import httpClient from '@/utils/httpclient';
	import Alert from '@components/model'

	export default {
		name: 'adminInvite',
		components: {
			Alert
		},
		data() {
			return {
				emails: [
					{
						name: ''
					},
					{
						name: '',
					}
				],
				btnActive: true,
				submitSuccess: false,
				showAlerts: {
					title: '',
					content: '',
					show: false,
					buttonText: ''
				},
              storeName:''
			}
		},
        mounted(){
          httpClient.post('/api/v1/user/store').then((res)=>{
            this.storeName = res.data.data.storeName;
          })
        },
		methods: {
			addInputBox() {
				this.emails.push({name: ''});
			},
			getAllEmails() {
				let emailsArray = [];
				let url = '/api/v1/inviteUser';
				this.emails.map((item, i) => {
					item.name.length ? emailsArray.push(item.name) : '';
				});
				httpClient.post(url, {emails: emailsArray.join(',')}).then((res) => {
					if (res.data.code === 200) {
						this.submitSuccess = !this.submitSuccess
					}
				})
			},
			verifyEmail(m) {//验证输入值，合法才可以提交
				let reg = /^.*@\w+(\.\w+)+/;
				if (!reg.test(m)) {
					let passData = {
						title: 'Invite User',
						show: true,
						content: 'The Email format is incorrect. Please re-enter',
						buttonText: {
							btnFirst: '',
							btnSecond: 'OK',
							userId: ''
						}
					};
					this.closeRemind(passData);
				} else {
					this.btnActive = false
				}
			},
			closeBox() {
				this.showAlerts.show = !this.showAlerts.show;
			},
			continueInvite() {
				this.submitSuccess = !this.submitSuccess;//点击continue，切换显示，清空之前的输入框值
				this.btnActive = !this.btnActive;//返回之后将按钮禁止
				this.emails.map((item, i) => {
					item.name = '';
				});
			},
			closeRemind(data) {
				this.showAlerts.show = data.show;
				this.showAlerts.title = data.title;
				this.showAlerts.content = data.content;
				this.showAlerts.buttonText = data.buttonText;
			},
		},
	}
